#include <stdio.h>
#include <string.h>
#include <time.h>

#define max(a,b) \
({ __typeof__ (a) _a = (a); \
   __typeof__ (b) _b = (b); \
 _a > _b ? _a : _b; })

#define RANGE  13

int main()
{

	clock_t begin, end;
	double time_spent;	
	begin = clock();

	int i = 0, j = 0;
	unsigned long max = 0;

	char* nb = "";

	while(nb[i] != '\0'){
		unsigned long tmp = 1;
		if (nb[i+RANGE-1] != '\0')
		{
			for (j = 0; j < RANGE; ++j)
			{
				
				tmp*= nb[i+j]- '0';
			}

			max = max(tmp, max);

		}else{

			for (j = 0; j < RANGE; ++j)
			{
				tmp*= nb[strlen(nb)-RANGE+j]- '0';
			}
			 
			max = max(tmp, max);
			break;
		}

		i++;
	}


	end = clock();
	time_spent = (double)(end - begin) / CLOCKS_PER_SEC;
	printf("time ! %f s\n", time_spent);

	printf("%lu\n", max);

	return 0;	

} 
